CREATE TABLE `portfolio` (`user_id` INT NOT NULL, `coin_id` VARCHAR(50) NOT NULL, `amount` DECIMAL(24, 8) NOT NULL, `avg_buy_price` DECIMAL(24, 8) NOT NULL, `last_updated` DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (`user_id`, `coin_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;